objectFunctions.functionBody:
def vo = newView('SpouseData')
vo.appendViewCriteria("surveyID = '"+surveyIDVar+"'")
vo.executeQuery()
if (!vo.hasNext()){
  r = vo.createRow()
    r.surveyID = surveyIDVar;

  r.SpouseIdNumber=idNumberSpouse;
  r.SpouseFirstName=fullNameSpouse;
  r.SpouseSurname=surnameSpouse;
  r.SpouseGender=genderSpouse;
  r.SpouseAge=ageSpouse;
  r.SpouseCitizenship=citizenshipSpouse;
  r.SpouseCitizenshipOther=citizenshipOtherSpouse;
  r.SpousePopGroup=popGroupSpouse;
  r.SpouseDisability=disabilitySpouse;
  r.SpouseDisabilityType=disabilityTypeSpouse;
  r.SpouseDisabilityOther=disabilityTypeOtherSpouse;
  r.SpouseHouseholdGrants=grantsSpouse;
  r.SpouseContactDetails=contactSpouse;
  r.SpouseHiEducation=eduSpouse;
  r.SpouseEmpStatus=empStatusSpouse;

  r.SpouseStateOldAgeWarVet=stateOldAgeWarVetSpouse;		
  r.SpouseDisabilityGrant=disabilityGrantSpouse;
  r.SpouseChildSupportGrant=childSupportGrantSpouse;
  r.SpouseChildFosterCareGr=childFosterCareGrSpouse;
  r.SpouseGrantInAid=grantInAidSpouse;
  r.SpouseCareDependencyGra=careDependencyGraSpouse;		
  r.SpouseSocialReliefGrant=socialReliefGrantSpouse;
  r.SpouseUnemploymentInsur=unemploymentInsurSpouse;

  r.governmentSubsidyStatusSpouse=governmentSubsidyStatusSpouse;

  vo.insertRow(r)
}
while (vo.hasNext()) {
  def r = vo.next();
  r.SpouseIdNumber=idNumberSpouse;
  r.SpouseFirstName=fullNameSpouse;
  r.SpouseSurname=surnameSpouse;
  r.SpouseGender=genderSpouse;
  r.SpouseAge=ageSpouse;
  r.SpouseCitizenship=citizenshipSpouse;
  r.SpouseCitizenshipOther=citizenshipOtherSpouse;
  r.SpousePopGroup=popGroupSpouse;
  r.SpouseDisability=disabilitySpouse;
  r.SpouseDisabilityType=disabilityTypeSpouse;
  r.SpouseDisabilityOther=disabilityTypeOtherSpouse;
  r.SpouseHouseholdGrants=grantsSpouse;
  r.SpouseContactDetails=contactSpouse;
  r.SpouseHiEducation=eduSpouse;
  r.SpouseEmpStatus=empStatusSpouse;
  r.SpouseStateOldAgeWarVet=stateOldAgeWarVetSpouse;		
  r.SpouseDisabilityGrant=disabilityGrantSpouse;
  r.SpouseChildSupportGrant=childSupportGrantSpouse;
  r.SpouseChildFosterCareGr=childFosterCareGrSpouse;
  r.SpouseGrantInAid=grantInAidSpouse;
  r.SpouseCareDependencyGra=careDependencyGraSpouse;		
  r.SpouseSocialReliefGrant=socialReliefGrantSpouse;
  r.SpouseUnemploymentInsur=unemploymentInsurSpouse;
  r.governmentSubsidyStatusSpouse=governmentSubsidyStatusSpouse;
}
